import style from './Slider.module.css';
import leftArrow from '../../img/arrowLeft.svg';
import rightArrow from '../../img/arrowRight.svg';
import { useState } from 'react';

const Slider = ({images}) => {

    const [currImg, setCurrImg] = useState(0);
    const length = images?.length;

    return(
        <div className={style.container}>
            <button><img src={leftArrow} alt="LA" /></button>
            {images.map((imagen, index) => {
                return(
                    <div>
                        <img className={style.sliderImg} key={index} src={imagen} alt='cesar' />
                    </div>
                )
            })}
            <button><img src={rightArrow} alt="RA" /></button>
        </div>
    )
}

export default Slider;
