import img1 from '../../img/img1.png';
import img2 from '../../img/img2.png';
import img3 from '../../img/img3.png';
import img4 from '../../img/img4.png';
import leftArrow from '../../img/arrowLeft.svg';
import rightArrow from '../../img/arrowRight.svg';
import style from './Slider.module.css';

export const Slider = () => {
    return(
        <div>
            <div className={style.slideContainer}>
                <div className={style.slide}>
                    <img className={style.mainImg} src={img1} alt="" />
                </div>
                <div className={style.slide}>
                    <img className={style.mainImg} src={img2} alt="" />
                </div>
                <div className={style.slide}>
                    <img className={style.mainImg} src={img3} alt="" />
                </div>
                <div className={style.slide}>
                    <img className={style.mainImg} src={img4} alt="" />
                </div>
            </div>
            <div>
                <button className={style.btn}>
                    <img src={leftArrow} alt="" />
                </button>
                <button className={style.btn}>
                    <img src={rightArrow} alt="" />
                </button>
            </div>
        </div>
    )
}
