import style from './Slider.module.css';
import leftArrow from '../../img/arrowLeft.svg';
import rightArrow from '../../img/arrowRight.svg';
import { useState } from 'react';

const Slider = ({images}) => {

    const [currImg, setCurrImg] = useState(0);
    const length = images?.length;

    const nextImg = () => {
        setCurrImg(currImg === length -1 ?
            0 : currImg +1)
    }

    const preImg = () => {
        setCurrImg(currImg === 0 ?
            length -1 : currImg +1)
    }

    return(
        <div className={style.container}>
            <button onClick={preImg}><img src={leftArrow} alt="LA" /></button>
            {images.map((imagen, index) => {
                return(
                    <div>{currImg === index &&
                        <img className={style.sliderImg} key={index} src={imagen} alt='cesar' />}
                    </div>
                )
            })}
            <button onClick={nextImg}><img src={rightArrow} alt="RA" /></button>
        </div>
    )
}

export default Slider;
